SELECT Н_УЧЕНИКИ.ИД, Н_ЛЮДИ.ФАМИЛИЯ, Н_ЛЮДИ.ИМЯ, Н_ЛЮДИ.ОТЧЕСТВО, Н_УЧЕНИКИ.ГРУППА, Н_УЧЕНИКИ.ЧЛВК_ИД, DATE_PART( 'year', now() ) - DATE_PART( 'year', Н_ЛЮДИ.ДАТА_РОЖДЕНИЯ ) AS ЧИСЛО_ЛЕТ FROM Н_УЧЕНИКИ JOIN Н_ЛЮДИ ON Н_УЧЕНИКИ.ЧЛВК_ИД = Н_ЛЮДИ.ИД WHERE Н_УЧЕНИКИ.ГРУППА = '3102' AND DATE_PART( 'year', now() ) - DATE_PART( 'year', Н_ЛЮДИ.ДАТА_РОЖДЕНИЯ ) > 20;

